import React, { useState, useEffect } from 'react';
import axios from 'axios';
import TodoItem from './TodoItem';
import TodoForm from './TodoForm';

const TodoList = ({ list }) => {
    const [todos, setTodos] = useState([]);

    useEffect(() => {
        fetchTodos();
    }, []);

    const fetchTodos = () => {
        axios.get(`http://localhost:8080/api/todolists/${list.id}`)
            .then(response => {
                setTodos(response.data.todos);
            })
            .catch(error => console.log(error));
    };

    const addTodo = (newTodo) => {
        setTodos([...todos, newTodo]);
    };

    const updateTodo = (updatedTodo) => {
        setTodos(todos.map(todo => (todo.id === updatedTodo.id ? updatedTodo : todo)));
    };

    const deleteTodo = (id) => {
        axios.delete(`http://localhost:8080/api/todos/${id}`)
            .then(() => {
                setTodos(todos.filter(todo => todo.id !== id));
            })
            .catch(error => console.log(error));
    };

    return (
        <div className="card my-4">
            <div className="card-header">
                <h2>{list.name}</h2>
            </div>
            <div className="card-body">
                <TodoForm listId={list.id} onAdd={addTodo} />
                <ul className="list-group">
                    {todos.map(todo => (
                        <TodoItem key={todo.id} todo={todo} onUpdate={updateTodo} onDelete={deleteTodo} />
                    ))}
                </ul>
            </div>
        </div>
    );
};

export default TodoList;
