import React, { useState, useEffect } from 'react';
import axios from 'axios';
import TodoItem from './TodoItem';
import TodoForm from './TodoForm';
import { Card, ListGroup, Button } from 'react-bootstrap';
import { toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

const TodoList = ({ list, onDelete }) => {
    const [todos, setTodos] = useState([]);

    useEffect(() => {
        fetchTodos();
    }, [list.id]);

    const fetchTodos = async () => {
        try {
            const response = await axios.get(`http://localhost:8080/api/todolists/${list.id}/todos`);
            if (response.data && response.data.todos) {
                setTodos(response.data.todos);
            } else {
                console.error('Invalid todos response:', response.data);
                setTodos([]);
            }
        } catch (error) {
            console.error('Error fetching todos:', error);
            setTodos([]);
            toast.error('Error fetching todos');
        }
    };

    const addTodo = (newTodo) => {
        setTodos([...todos, newTodo]);
    };

    const updateTodo = (updatedTodo) => {
        setTodos(todos.map(todo => (todo.id === updatedTodo.id ? updatedTodo : todo)));
    };

    const deleteTodo = async (id) => {
        try {
            await axios.delete(`http://localhost:8080/api/todos/${id}`);
            setTodos(todos.filter(todo => todo.id !== id));
            toast.success('Todo deleted successfully');
        } catch (error) {
            console.error('Error deleting todo:', error);
            toast.error('Error deleting todo');
        }
    };

    const handleDeleteList = () => {
        onDelete(list.id);
    };

    return (
        <Card className="my-4">
            <Card.Header className="d-flex justify-content-between align-items-center">
                <h2>{list.name}</h2>
                <Button variant="danger" size="sm" onClick={handleDeleteList}>Delete List</Button>
            </Card.Header>
            <Card.Body>
                <TodoForm listId={list.id} onAdd={addTodo} />
                <ListGroup>
                    {todos.map(todo => (
                        <TodoItem key={todo.id} todo={todo} onUpdate={updateTodo} onDelete={deleteTodo} />
                    ))}
                </ListGroup>
            </Card.Body>
        </Card>
    );
};

export default TodoList;
