import React from 'react';
import axios from 'axios'; // Import axios
import { ListGroup, Button } from 'react-bootstrap';

const TodoItem = ({ todo, onUpdate, onDelete }) => {
    const handleToggleCompletion = async () => {
        const updatedTodo = { ...todo, completed: !todo.completed };
        try {
            const response = await axios.put(`http://localhost:8080/api/todos/${todo.id}`, updatedTodo);
            onUpdate(response.data);
        } catch (error) {
            console.error('Error updating todo:', error);
        }
    };

    return (
        <ListGroup.Item className={todo.completed ? 'completed' : ''}>
            <input
                type="checkbox"
                checked={todo.completed}
                onChange={handleToggleCompletion}
            />
            {todo.title}
            <Button variant="danger" size="sm" onClick={() => onDelete(todo.id)} className="float-right">
                Delete
            </Button>
        </ListGroup.Item>
    );
};

export default TodoItem;
