import React, { useState } from 'react';
import axios from 'axios';
import { Button } from 'react-bootstrap';

const TodoItem = ({ todo, onUpdate, onDelete }) => {
    const [isEditing, setIsEditing] = useState(false);
    const [editedTitle, setEditedTitle] = useState(todo.title);

    const handleComplete = async () => {
        const updatedTodo = { ...todo, completed: !todo.completed };
        try {
            const response = await axios.put(`http://localhost:8080/api/todos/${todo.id}`, updatedTodo);
            onUpdate(response.data);
        } catch (error) {
            console.error('Error updating todo:', error);
        }
    };

    const handleDelete = async () => {
        try {
            await axios.delete(`http://localhost:8080/api/todos/${todo.id}`);
            onDelete(todo.id);
        } catch (error) {
            console.error('Error deleting todo:', error);
        }
    };

    const handleEdit = () => {
        setIsEditing(true);
    };

    const handleSave = async () => {
        const updatedTodo = { ...todo, title: editedTitle };
        try {
            const response = await axios.put(`http://localhost:8080/api/todos/${todo.id}`, updatedTodo);
            onUpdate(response.data);
            setIsEditing(false);
        } catch (error) {
            console.error('Error updating todo:', error);
        }
    };

    const handleCancel = () => {
        setIsEditing(false);
        setEditedTitle(todo.title);
    };

    return (
        <li className={`list-group-item d-flex justify-content-between align-items-center ${todo.completed ? 'completed' : ''}`}>
            <div>
                <input 
                    type="checkbox"
                    checked={todo.completed}
                    onChange={handleComplete}
                />
                {isEditing ? (
                    <input 
                        type="text"
                        value={editedTitle}
                        onChange={(e) => setEditedTitle(e.target.value)}
                    />
                ) : (
                    <span className="mx-2">{todo.title}</span>
                )}
            </div>
            <div>
                {isEditing ? (
                    <>
                        <Button variant="success" size="sm" onClick={handleSave}>Save</Button>
                        <Button variant="secondary" size="sm" onClick={handleCancel}>Cancel</Button>
                    </>
                ) : (
                    <>
                        <Button variant="primary" size="sm" onClick={handleEdit}>Edit</Button>
                        <Button variant="danger" size="sm" onClick={handleDelete}>Delete</Button>
                    </>
                )}
            </div>
        </li>
    );
};

export default TodoItem;
