import React, { useState } from 'react';
import { ListGroup, Button, Form } from 'react-bootstrap';
import { toast } from 'react-toastify';

const TodoItem = ({ todo, onUpdate, onDelete }) => {
    const [isEditing, setIsEditing] = useState(false);
    const [title, setTitle] = useState(todo.title);
    const [errorMessage, setErrorMessage] = useState('');

    const handleEdit = () => {
        setIsEditing(true);
    };

    const handleSave = async () => {
        if (!title.trim()) {
            setErrorMessage("Don't leave it empty");
            return;
        }
        setErrorMessage('');
        try {
            const updatedTodo = { ...todo, title };
            await onUpdate(updatedTodo);
            setIsEditing(false);
            toast.success('Todo updated successfully');
        } catch (error) {
            console.error('Error updating todo:', error);
            toast.error('Error updating todo');
        }
    };

    const handleCancel = () => {
        setTitle(todo.title);
        setIsEditing(false);
        setErrorMessage('');
    };

    return (
        <ListGroup.Item className={todo.completed ? 'completed d-flex justify-content-between align-items-center' : 'd-flex justify-content-between align-items-center'}>
            {isEditing ? (
                <Form inline onSubmit={(e) => { e.preventDefault(); handleSave(); }} className="w-100 d-flex align-items-center">
                    <Form.Control
                        type="text"
                        value={title}
                        onChange={(e) => setTitle(e.target.value)}
                        className="mr-2 flex-grow-1"
                    />
                    {errorMessage && <Form.Text className="text-danger">{errorMessage}</Form.Text>}
                    <Button variant="success" onClick={handleSave} className="mr-2">Save</Button>
                    <Button variant="secondary" onClick={handleCancel}>Cancel</Button>
                </Form>
            ) : (
                <div className="w-100 d-flex justify-content-between align-items-center">
                    <span className="flex-grow-1">{todo.title}</span>
                    <div>
                        <Button variant="primary" onClick={handleEdit} className="ml-2">Edit</Button>
                        <Button variant="danger" onClick={() => onDelete(todo.id)} className="ml-2">Delete</Button>
                    </div>
                </div>
            )}
        </ListGroup.Item>
    );
};

export default TodoItem;
