import React, { useState, useEffect } from 'react';
import axios from 'axios';
import TodoList from './TodoList';

const TodoListManager = () => {
    const [lists, setLists] = useState([]);
    const [newListName, setNewListName] = useState('');

    useEffect(() => {
        fetchLists();
    }, []);

    const fetchLists = () => {
        axios.get('http://localhost:8080/api/todolists')
            .then(response => {
                console.log('Response from server:', response.data); // Adaugă acest log
                setLists(response.data);
            })
            .catch(error => {
                console.log(error);
                setLists([]); // Inițializează ca un array gol în caz de eroare
            });
    };

    const handleCreateList = () => {
        axios.post('http://localhost:8080/api/todolists', { name: newListName })
            .then(response => {
                setLists([...lists, response.data]);
                setNewListName('');
            })
            .catch(error => console.log(error));
    };

    return (
        <div className="container">
            <h1 className="text-center my-4">Todo List Manager</h1>
            <div className="mb-4">
                <input 
                    type="text"
                    className="form-control"
                    value={newListName}
                    onChange={(e) => setNewListName(e.target.value)}
                    placeholder="New List Name"
                />
                <button className="btn btn-primary mt-2" onClick={handleCreateList}>Create List</button>
            </div>
            {Array.isArray(lists) && lists.map(list => (
                <TodoList key={list.id} list={list} />
            ))}
        </div>
    );
};

export default TodoListManager;
