import React, { useState, useEffect } from 'react';
import axios from 'axios';
import TodoItem from './TodoItem';
import TodoForm from './TodoForm';

const TodoList = ({ list, onDelete }) => {
    const [todos, setTodos] = useState([]);

    useEffect(() => {
        fetchTodos();
    }, []);

    const fetchTodos = async () => {
        try {
            const response = await axios.get(`http://localhost:8080/api/todolists/${list.id}`);
            console.log('Todos for list:', list.id, response.data);
            if (response.data && response.data.todos) {
                setTodos(response.data.todos);
            } else {
                console.error('Invalid todos response:', response.data);
                setTodos([]);
            }
        } catch (error) {
            console.error('Error fetching todos:', error);
            setTodos([]);
        }
    };

    const addTodo = (newTodo) => {
        setTodos([...todos, newTodo]);
    };

    const updateTodo = (updatedTodo) => {
        setTodos(todos.map(todo => (todo.id === updatedTodo.id ? updatedTodo : todo)));
    };

    const deleteTodo = async (id) => {
        try {
            await axios.delete(`http://localhost:8080/api/todos/${id}`);
            setTodos(todos.filter(todo => todo.id !== id));
        } catch (error) {
            console.error('Error deleting todo:', error);
        }
    };

    const handleDeleteList = () => {
        onDelete(list.id);
    };

    return (
        <div className="card my-4">
            <div className="card-header d-flex justify-content-between align-items-center">
                <h2>{list.name}</h2>
                <button className="btn btn-danger btn-sm" onClick={handleDeleteList}>Delete List</button>
            </div>
            <div className="card-body">
                <TodoForm listId={list.id} onAdd={addTodo} />
                <ul className="list-group">
                    {todos.map(todo => (
                        <TodoItem key={todo.id} todo={todo} onUpdate={updateTodo} onDelete={deleteTodo} />
                    ))}
                </ul>
            </div>
        </div>
    );
};

export default TodoList;
