import React, { useState, useEffect } from 'react';
import axios from 'axios';
import TodoList from './TodoList';
import './TodoListManager.css';
import { Sidebar, Menu, MenuItem, SubMenu } from 'react-pro-sidebar';
import { FaTrash, FaPlus, FaSearch } from 'react-icons/fa';

const TodoListManager = () => {
    const [lists, setLists] = useState([]);
    const [newListName, setNewListName] = useState('');
    const [selectedListId, setSelectedListId] = useState(null);
    const [searchTerm, setSearchTerm] = useState('');

    useEffect(() => {
        fetchLists();
    }, []);

    const fetchLists = async () => {
        try {
            const response = await axios.get('http://localhost:8080/api/todolists');
            console.log('Response from server:', response.data);
            setLists(response.data);
        } catch (error) {
            console.error('Error fetching todo lists:', error);
        }
    };

    const createList = async () => {
        if (newListName.trim() === '') {
            alert("Don't leave it empty");
            return;
        }
        try {
            const response = await axios.post('http://localhost:8080/api/todolists', { name: newListName });
            setNewListName('');
            fetchLists();
        } catch (error) {
            console.error('Error creating list:', error);
        }
    };

    const deleteList = async (listId) => {
        if (window.confirm('Are you sure you want to delete this list?')) {
            try {
                await axios.delete(`http://localhost:8080/api/todolists/${listId}`);
                fetchLists();
            } catch (error) {
                console.error('Error deleting list:', error);
            }
        }
    };

    const filteredLists = lists.filter(list =>
        list.name.toLowerCase().includes(searchTerm.toLowerCase())
    );

    const handleSearchChange = (event) => {
        setSearchTerm(event.target.value);
    };

    const handleNewListNameChange = (event) => {
        setNewListName(event.target.value);
    };

    return (
        <div className="todo-list-manager">
            <Sidebar>
                <Menu iconShape="square">
                    <MenuItem icon={<FaSearch />}>
                        <input
                            type="text"
                            placeholder="Search..."
                            value={searchTerm}
                            onChange={handleSearchChange}
                            className="search-input"
                        />
                    </MenuItem>
                    {filteredLists.map(list => (
                        <MenuItem key={list.id} onClick={() => setSelectedListId(list.id)}>
                            {list.name}
                            <FaTrash className="delete-icon" onClick={() => deleteList(list.id)} />
                        </MenuItem>
                    ))}
                    <SubMenu title="Create New List" icon={<FaPlus />}>
                        <div className="create-list-form">
                            <input
                                type="text"
                                placeholder="New list name"
                                value={newListName}
                                onChange={handleNewListNameChange}
                                className="new-list-input"
                            />
                            <button onClick={createList} className="create-list-button">Create</button>
                        </div>
                    </SubMenu>
                </Menu>
            </Sidebar>
            <main>
                {selectedListId && (
                    <TodoList listId={selectedListId} />
                )}
            </main>
        </div>
    );
};

export default TodoListManager;
